SELECT a.id,u.username,t.`name` tname,a.centent,a.`name` ,a.start_time startTime,a.end_time endTime,a.number from activities a INNER JOIN type t ON a.type_id =t.id INNER JOIN `user` u ON a.user_id=u.id
WHERE u.username REGEXP #{keyword} or t.`name` REGEXP #{keyword} or a.`name` REGEXP #{keyword} or a.centent REGEXP #{keyword}
limit #{skipNum},#{takeNum}

SELECT a.id,u.username,t.`name` tname,a.centent,a.`name` ,a.start_time startTime,a.end_time endTime,a.number
from activities a INNER JOIN type t ON a.type_id =t.id INNER
JOIN `user` u ON a.user_id=u.id WHERE a.id = #{id,jdbcType=VARCHAR}
