mapper.update:
UPDATE activity_registration
SET per_id=#{perId},
activities_id=#{activitiesId},
registration_date=#{registrationDate},
appointment=#{appointment}
where id=#{id}

mapper.select:
SELECT  personal_information.name personalInformationName,activities.name activitiesName,activity_registration.registration_date registrationDate,activity_registration.id,activity_registration.appointment 
		FROM activity_registration JOIN personal_information ON activity_registration.per_id=personal_information.id
		JOIN activities ON activity_registration.activities_id=activities.id 
		
		 WHERE personal_information.name REGEXP #{keyword} or 
		 activities.name REGEXP #{keyword}
		
		LIMIT #{skipNum},#{takeNum}
