mapper.update:
UPDATE business
SET name=#{name},
idcard=#{idcard},
shopname=#{shopname},
shop_material_path=#{shopMaterialPath}
WHERE id=#{id}

mapper.select:

	select id,name,idcard,shopname,shop_material_path shopMaterialPath
	from business
   
	
	where name REGEXP #{keyword} or shopname REGEXP #{keyword} or idcard REGEXP #{keyword} 
	
 	LIMIT #{skipNum},#{takeNum}

SELECT
COUNT(*) FROM business

where name REGEXP #{keyword} or shopname REGEXP #{keyword} or idcard REGEXP #{keyword}

select id,name,idcard,shopname,shop_material_path shopMaterialPath
from business
where id=#{id}
