mapper.select:
select buyers.id,personal_information.name pname,current_integral currentIntegral, historical_integral historicalIntegral, grade grade,delivery_address_id deliveryAddressId,information_id informationId
from buyers,personal_information
from buyers JOIN personal_information ON buyers.id=personal_information.id where personal_information.name REGEXP #{keyword} or buyers.grade REGEXP #{keyword}
limit #{skipNum},#{takeNum}

select.if:
select count(*) from buyers  
JOIN personal_information ON buyers.id=personal_information.id 
where personal_information.name REGEXP #{keyword} or buyers.grade REGEXP #{keyword}
