mapper.update:
UPDATE health_records set
per_id=#{perId}, registr_date=#{registrDate}, user_id=#{userId}, disease_id=#{diseaseId}, shape=#{shape}, diet_situation=#{dietSituation}, sports_situation=#{sportsSituation}, emotion_control=#{emotionControl},thrombus=#{thrombus}, blood_pressure=#{bloodPressure}, health_assessment=#{healthAssessment}, dangerous_tips=#{dangerousTips}, conclusion_suggestion=#{conclusionSuggestion}, evaluate=#{evaluate}
where id =#{id}

mapper.select:
select p.name pname, h.registr_date registrDate, u.username username, t.name diseaseId, h.shape, h.diet_situation dietSituation, h.sports_situation sportsSituation, h.emotion_control emotionControl, h.thrombus, h.blood_pressure bloodPressure, h.health_assessment healthAssessment, h.dangerous_tips dangerousTips, h.conclusion_suggestion conclusionSuggestion, h.evaluate from health_records h JOIN personal_information p ON h.per_id=p.id JOIN `user` u ON h.user_id=u.username JOIN type t on h.disease_id=t.id WHERE p.name REGEXP #{keyword} or u.username REGEXP #{keyword}
select p.name pname, h.registr_date registrDate, u.username username, t.name diseaseId, h.shape, h.diet_situation dietSituation, h.sports_situation sportsSituation, h.emotion_control emotionControl, h.thrombus, h.blood_pressure bloodPressure, h.health_assessment healthAssessment, h.dangerous_tips dangerousTips, h.conclusion_suggestion conclusionSuggestion, h.evaluate from health_records h JOIN personal_information p ON h.per_id=p.id JOIN `user` u ON h.user_id=u.username JOIN type t on h.disease_id=t.id
limit #{skipNum},#{takeNum}

select COUNT(*) from health_records h
JOIN personal_information ON h.per_id=personal_information.id JOIN `user` ON h.user_id=`user`.username JOIN type t on h.disease_id=t.id WHERE personal_information.name REGEXP #{keyword} or `user`.username REGEXP #{keyword}
JOIN personal_information ON h.per_id=personal_information.id JOIN `user` ON h.user_id=`user`.username JOIN type t on h.disease_id=t.id
