mapper.select:
SELECT id,per_id perId, name,telephone,address_id
addressId,relationship_id relationshipId FROM relatives WHERE
per_id=#{perId}

SELECT relatives.name relName,personal_information.name perName,type.`name` tyName, relatives.telephone relPhone,relatives.id, relatives.address_id,relatives.per_id,relatives.relationship_id FROM relatives JOIN personal_information ON relatives.per_id=personal_information.id JOIN type ON relatives.relationship_id = type.id
WHERE relatives.name REGEXP#{keyword} or personal_information.name REGEXP #{keyword} or type.`name` REGEXP #{keyword} or relatives.telephone REGEXP #{keyword}
limit #{skipNum},#{takeNum}
