mapper.select:
SELECT id,per_id perId, name,telephone,address_id
addressId,relationship_id relationshipId FROM relatives WHERE
per_id=#{perId}

SELECT r.id id,p.`name` perId,r.`name` name,r.telephone telephone,t.`name` relationshipId FROM relatives r JOIN personal_information p on r.per_id=p.id JOIN type t on r.relationship_id=t.id
WHERE r.name REGEXP#{keyword} or p.name REGEXP #{keyword} or t.`name` REGEXP #{keyword} or relatives.telephone REGEXP #{keyword}
limit #{skipNum},#{takeNum}
